import React from 'react'
import { Form, Icon, Input, Button, Checkbox } from 'antd';
import { BrowserRouter as Router, Route, Link, Redirect, withRouter } from "react-router-dom";
import http from '../../api/http.js'
import './login.css';
import { islogin, userlogin } from '../../api/request.js'
class login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            ds: ''
        };
    }
    componentWillMount() {
        islogin().then(res => {
            if (res.islogin) return <Redirect to={{ pathname: "/home" }} />;
        })
    }
    componentDidMount() {
        let finalNum = "";
        for (let i = 0; i < 8; i++) {
            finalNum += Math.floor(Math.random() * 10);
        }
        this.setState({
            ds: finalNum
        });
    }
    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (!err) {
                console.log('Received values of form: ', values);
                values.ds = this.state.ds
                userlogin({
                    ...values
                }).then(res => {
                    localStorage.setItem('Token', res.token)
                })

            }
        });
    };
    changeCode = e => {
        let finalNum = "";
        for (let i = 0; i < 8; i++) {
            finalNum += Math.floor(Math.random() * 10);
        }
        this.setState({
            ds: finalNum
        });
        e.target.src = http.baseURL + `/kaptcha/${finalNum}`
    }
    render() {
        const { getFieldDecorator } = this.props.form;
        return (
            <div className='centerform'>
                <div className='welcome'>欢迎登录</div>
                <Form onSubmit={this.handleSubmit} className="login-form">
                    <Form.Item>
                        {getFieldDecorator('taxpayId', {
                            rules: [{ required: true, message: 'Please input your username!' }],
                        })(
                            <Input
                                prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
                                placeholder="Username"
                            />,
                        )}
                    </Form.Item>
                    <Form.Item >
                        {getFieldDecorator('password', {
                            rules: [{ required: true, message: 'Please input your Password!' }],
                        })(
                            <Input
                                prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />}
                                type="password"
                                placeholder="Password"
                            />
                        )}
                    </Form.Item>

                    <Form.Item>

                        {getFieldDecorator('verify', {
                            rules: [{ required: true, message: 'Please input your Password!' }],
                        })(
                            <Input
                                style={{ width: '50%' }}
                                placeholder="验证码"
                            />
                        )}
                        < img style={{ float: 'right' }} src={http.baseURL + `/kaptcha/${this.state.ds}`} alt='暂无图片' onClick={this.changeCode} />
                    </Form.Item>
                    <Form.Item>
                        {/* {getFieldDecorator('remember', {
                            valuePropName: 'checked',
                            initialValue: true,
                        })(<Checkbox>Remember me</Checkbox>)} */}
                        {/* <a className="login-form-forgot" href=""> Forgot password</a> */}
                        <Button type="primary" htmlType="submit" className="login-form-button">登录</Button>
                        {/* Or <a href="">register now!</a> */}
                    </Form.Item>
                </Form>
            </div>
        );
    }
}
login = Form.create({})(login)
export default login;
