import React from 'react'
import { Layout, Menu, Avatar, Icon, Dropdown } from 'antd';
import { BrowserRouter as Router, Route, Link, Redirect, withRouter, } from "react-router-dom";
import './home.css';
import routes from '../../routes/index.js';
const { Header, Content, Sider } = Layout;
const { SubMenu } = Menu;
class home extends React.Component {
    state = {
        collapsed: false,
    };
    handle = e => {
        console.log(e);
        if (e.key == "1") {
            //跳转到path页面
            this.props.history.push('/home')
        } else {
            // browserHistory.push("/tab2");
        }

    };
    render() {
        const menu = (
            <Menu  >
                <Menu.Item key="0">
                    <a target="_blank" rel="noopener noreferrer" href="http://www.alipay.com/">
                        1st menu item
      </a>
                </Menu.Item>
                <Menu.Item key="1">
                    <a target="_blank" rel="noopener noreferrer" href="http://www.taobao.com/">
                        2nd menu item
      </a>
                </Menu.Item>
                <Menu.Divider />
                <Menu.Item key="3" disabled>
                    3rd menu item（disabled）
    </Menu.Item>
            </Menu>
        );
        console.log(routes)
        return (
            <Router>
                <div>
                    <Header style={{ minWidth: '100%' }} className="header">
                        <div className="logo" >
                            <img style={{ width: '120px' }} src={require("../../assets/images/logo.png")}></img>
                        </div>
                        <div className="topmenu">
                            <Menu
                                onClick={this.handle}
                                theme="dark"
                                mode="horizontal"
                                defaultSelectedKeys={['1']}
                                style={{ lineHeight: '64px' }}
                            >
                                <Menu.Item className='topmenuitem' key="1"> <Link to="/home"><Icon type="home" /> 首页</Link></Menu.Item>
                                <Menu.Item className='topmenuitem' key="2"> <Link to="/user"><Icon type="desktop" />工作</Link></Menu.Item>
                                <Menu.Item className='topmenuitem' key="3"> <Link to="/shop"><Icon type="setting" />商户</Link></Menu.Item>
                            </Menu>
                        </div>
                        <div className="topmsg" >
                            <Dropdown overlay={menu}>
                                <Avatar size={32} icon="user" />
                            </Dropdown>,
                    </div>
                    </Header>
                </div>
                <Layout style={{ minHeight: '100vh' }}>
                    <Layout style={{ minWidth: '100%' }}>
                        <Sider width={200} style={{ background: '#fff' }}>
                            <Menu
                                mode="inline"
                                defaultSelectedKeys={['1']}
                                defaultOpenKeys={['sub1']}
                                style={{ height: '100%', borderRight: 0 }}
                            >
                                <SubMenu
                                    key="sub1"
                                    title={
                                        <span>
                                            <Icon type="user" />
                                            subnav 1
              </span>
                                    }
                                >
                                    <Menu.Item key="1">option1</Menu.Item>
                                    <Menu.Item key="2">option2</Menu.Item>
                                    <Menu.Item key="3">option3</Menu.Item>
                                    <Menu.Item key="4">option4</Menu.Item>
                                </SubMenu>
                                <SubMenu
                                    key="sub2"
                                    title={
                                        <span>
                                            <Icon type="laptop" />
                                            subnav 2
              </span>
                                    }
                                >
                                    <Menu.Item key="5">option5</Menu.Item>
                                    <Menu.Item key="6">option6</Menu.Item>
                                    <Menu.Item key="7">option7</Menu.Item>
                                    <Menu.Item key="8">option8</Menu.Item>
                                </SubMenu>
                                <SubMenu
                                    key="sub3"
                                    title={
                                        <span>
                                            <Icon type="notification" />
                                            subnav 3
              </span>
                                    }
                                >
                                    <Menu.Item key="9">option9</Menu.Item>
                                    <Menu.Item key="10">option10</Menu.Item>
                                    <Menu.Item key="11">option11</Menu.Item>
                                    <Menu.Item key="12">option12</Menu.Item>
                                </SubMenu>
                            </Menu>
                        </Sider>
                        <Layout style={{ padding: '24px' }}>
                            <Content
                                style={{
                                    background: '#fff',
                                    padding: 24,
                                    margin: 0,
                                    minHeight: 280,
                                }}
                            >
                                Content
                          </Content>
                        </Layout>
                    </Layout>
                </Layout>
            </Router>

        );
    }
}

export default home;
